angular.module("angular-ads",[]).directive("ogAds",function(){var a="<div class='angular-ads' ng-cloak><div class='og-ad' ng-repeat='ad in ads'><div class='og-ad-header' ng-class='{adborder:{{!$first}}}'><div class='og-ad-headline'><a ng-href='{{ad.link}}'>{{ad.headline}}</a></div><div class='og-ad-website'>{{webLink(ad.link)}}</div></div><div class='og-ad-caption'><div class='og-ad-img'> <a ng-href='{{ad.link}}'><img ng-src='{{ad.imageUrl}}'></a></div><div class='og-ad-text'><a ng-href='{{ad.link}}'>{{ad.text}}</a></div></div></div></div>";return{restrict:"A",template:a,replace:true,scope:{ogAdsUrl:"@ogAdsUrl"},controller:["$scope","adverts",function(b,c){b.ads=[];b.webLink=function(e){var d,f;d=document.createElement("a");d.href=e;f=d.hostname;f=f.replace(/^www\./,"");return f};if(b.ogAdsUrl){return c.getCustomAds(b.ogAdsUrl).success(function(d){return b.ads=d})}else{return c.getAds().success(function(d){return b.ads=d})}}]}}).factory("adverts",function(a){getCustomAds=function(b){return a.get(b)};getAds=function(){return a.get("ads/ads.json")};return{getCustomAds:getCustomAds,getAds:getAds}});